import React, { Component } from "react";
import Cardlist from "./cardlist"
import { services } from "./services";
import SearchBox from './SearchBox';

class App extends Component {
    constructor(){
        super()
        this.state = {
            services: services,
            Searchfield: ''
        }
    }

    onSearchChange = (event) => {
        this.setState({Searchfield: event.target.value})
        //console.log(event.target.value);
    }

    render() {
        const filterServices = this.state.services.filter(services => {
            return services.username.toLowerCase().includes(this.state.Searchfield.toLowerCase)
        })
        return(
            <div className="tc">
                <h1>Robotfriends</h1>
                <SearchBox SearchChange={this.onSearchChange} />
                <Cardlist services={filterServices} />
            </div>
        );
    }
}

export default App;
